mapper.select:
select * from article_collect
where article_id = #{articleId}
and user_id = #{userId}

mapper.insert:
insert into article_collect (
    article_id, user_id, collect_time, is_deleted
) values (
    #{articleId}, #{userId}, #{collectTime}, #{isDeleted}
)

mapper.update:
update article_collect
set is_deleted = 1
where article_id = #{articleId}
and user_id = #{userId}

update article_collect
set is_deleted = 0
where article_id = #{articleId}
and user_id = #{userId}
