mapper.select:
select * from article_like
where article_id = #{articleId}
and user_id = #{userId}

mapper.insert:
insert into article_like (
    article_id, user_id, create_time, is_deleted
) values (
    #{articleId}, #{userId}, #{createTime}, #{isDeleted}
)

mapper.update:
update article_like
set is_deleted = 1
where article_id = #{articleId}
and user_id = #{userId}

update article_like
set is_deleted = 0
where article_id = #{articleId}
and user_id = #{userId}
