mapper.insert:
insert into article (
    title, content, cover_img, state,
    category_id, create_user, create_time, update_time
) values (
             #{title}, #{content}, #{coverImg}, #{state},
             #{categoryId}, #{createUser}, #{createTime}, #{updateTime}
         )

mapper.select:
select * from article


        category_id=#{categoryId}


        and state=#{state}

    and create_user=#{userId}

SELECT a.id, a.title, a.cover_img as coverImg, SUBSTRING(a.content, 1, 100) as summary, u.username as author, a.create_time as createTime, a.state, a.category_id as categoryId, COALESCE(a.like_count, 0) as likeCount, COALESCE(a.collect_count, 0) as collectCount, (SELECT COUNT(*) FROM article_comment ac WHERE ac.article_id = a.id AND ac.is_deleted = 0) as commentCount FROM article a LEFT JOIN user u ON a.create_user = u.id WHERE a.state = #{state}
ORDER BY (COALESCE(a.like_count, 0) + COALESCE(a.collect_count, 0)) DESC, a.create_time DESC
ORDER BY a.create_time DESC
LIMIT #{offset}, #{pageSize}

SELECT COUNT(*)
FROM article a
WHERE a.state = #{state}

mapper.update:
update article
set like_count = ifnull(like_count, 0) - 1
where id = #{articleId}

update article
set like_count = ifnull(like_count, 0) + 1
where id = #{articleId}

update article
set collect_count = ifnull(collect_count, 0) + 1
where id = #{articleId}

update article
set collect_count = ifnull(collect_count, 0) - 1
where id = #{articleId}

mapper.select:
select * from article
where create_user = #{userId}

    and state = #{state}

order by create_time desc
limit #{offset}, #{pageSize}

select count(*) from article
where create_user = #{userId}

    and state = #{state}
