mapper.insert:
insert into author_apply (
    user_id, real_name, id_card, apply_desc, 
    status, create_time, audit_time, audit_user_id, reject_reason
) values (
    #{userId}, #{realName}, #{idCard}, #{applyDesc}, 
    #{status}, #{createTime}, #{auditTime}, #{auditUserId}, #{rejectReason}
)

mapper.select:
SELECT a.id, a.real_name as realName, a.id_card as idCard, a.apply_desc as applyDesc, a.status, a.create_time as createTime, u.id as "userInfo.id", u.username as "userInfo.username", u.nickname as "userInfo.nickname", u.email as "userInfo.email", u.user_pic as "userInfo.userPic" FROM author_apply a LEFT JOIN USER u ON a.user_id = u.id
AND a.status = #{status}
ORDER BY a.create_time DESC LIMIT #{start}, #{pageSize}

SELECT COUNT(*)
FROM author_apply a


        AND a.status = #{status}
