mapper.select:
select * from article_comment_like
where comment_id = #{commentId}
and user_id = #{userId}

mapper.insert:
insert into article_comment_like (
    comment_id, user_id, create_time, is_deleted
) values (
    #{commentId}, #{userId}, #{createTime}, #{isDeleted}
)

mapper.update:
update article_comment_like
set is_deleted = 1
where comment_id = #{commentId}
and user_id = #{userId}

update article_comment_like
set is_deleted = 0
where comment_id = #{commentId}
and user_id = #{userId}

update article_comment
set comment_like_count = comment_like_count + #{increment}
where id = #{commentId}
